"use strict";

var mongoose = require('mongoose');

var Schema = mongoose.Schema({

    season: Number,
    game_id: Number,
    date: Date,
    media: String,
    game_info: {
        red_team: {
            id: Number,
            entry: Array,
            win: Boolean,
        },

        blue_team: {
            id: Number,
            entry: Array,
            win: Boolean,
        },

        round_info: Array,
        media: String,
    },
    expired: {
        type: Boolean,
        default: false

    },
    done: {
        type: Boolean,
        default: false

    }

}, {
    timestamps: true
});

module.exports = mongoose.model("games", Schema);