"use strict";

var mongoose = require('mongoose');

var Schema = mongoose.Schema({

    teamid: {
        type: Number,
        required: true,
        unique: true
    },
    season: {
        type: Number,
        required: true,
    },
    info: {
        name: String,
        logo: String,
        leader: String,
        comment: String,

        leagueSummary: {
            win: Number,
            lose: Number,
        },
    },


}, {
    timestamps: true
});

module.exports = mongoose.model("teams", Schema);